import React, { useState, useEffect } from 'react';
import PersonalInfoStep from './steps/PersonalInfoStep';
import { submitForm } from '../services/firebaseService';
import { useAuth } from '../contexts/AuthContext';
import { signOutUser } from '../services/authService';

const MultiStepForm = () => {
  const [formData, setFormData] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');
  const [userId, setUserId] = useState('');
  const { user, userId: authUserId } = useAuth();

  // Generate or retrieve user ID (fallback for when auth is not enabled)
  useEffect(() => {
    let storedUserId = localStorage.getItem('formUserId');
    if (!storedUserId) {
      // Generate a unique user ID
      storedUserId = 'user_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
      localStorage.setItem('formUserId', storedUserId);
    }
    setUserId(storedUserId);
  }, []);

  const handleLogout = async () => {
    await signOutUser();
  };

  // TODO: Implement form validation using Formik and Yup
  // TODO: Implement form data handling

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitMessage('');

    try {
      // TODO: Add validation before submitting
      const submissionData = {
        ...formData,
        userId: userId
      };

      const result = await submitForm(submissionData);

      if (result.success) {
        setSubmitMessage('Form submitted successfully!');
        // Reset form
        setFormData({});
      } else {
        setSubmitMessage(result.message);
      }
    } catch (error) {
      setSubmitMessage('An error occurred. Please try again.');
      console.error('Submit error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h1>Personal Information Form</h1>
          <button 
            onClick={handleLogout}
            className="btn btn-secondary"
            style={{ fontSize: '14px', padding: '8px 16px' }}
          >
            Logout
          </button>
        </div>
        <p>Please provide your basic personal details.</p>

        {user && (
          <div style={{ 
            marginBottom: '20px', 
            padding: '10px', 
            backgroundColor: '#e3f2fd', 
            borderRadius: '4px',
            fontSize: '14px'
          }}>
            <strong>Logged in as:</strong> {user.email}<br/>
            <strong>Your Candidate ID:</strong> {userId}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <PersonalInfoStep 
            formData={formData} 
            setFormData={setFormData} 
          />

          {submitMessage && (
            <div className={`submit-message ${submitMessage.includes('successfully') ? 'success' : 'error'}`}>
              {submitMessage}
            </div>
          )}

          <div className="form-actions">
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Submitting...' : 'Submit'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default MultiStepForm;
