const FileUploadStep = () => {
  return (
    <div>
      <h2>File Upload</h2>
      <p>Upload your documents and images. Maximum file size: 5MB per file.</p>

      {/* TODO: Implement file upload functionality:
          - Drag & drop file upload using react-dropzone
          - Support for multiple file types (images, PDFs, text files)
          - File size validation (5MB max per file)
          - File removal functionality
          - Upload progress indicators
          - File list display
      */}

      <div className="file-upload-area">
        <p>Drag & drop files here, or click to select files</p>
        <p style={{ fontSize: '14px', color: '#666', marginTop: '8px' }}>
          Supported formats: Images (JPEG, PNG, GIF), PDF, Text files
        </p>
        {/* TODO: Add file input and dropzone functionality */}
      </div>

      {/* TODO: Add file list display with remove functionality */}
    </div>
  );
};

export default FileUploadStep;
