import React, { useState, useRef } from "react";
import "./App.css";

// HPAIR Personal Information Form — Harvard Themed, High Fidelity UI/UX
export default function App() {
  const [data, setData] = useState({
    address: "",
    phone: "",
    nationality: "",
    preferredLanguage: "",
    hasLinkedIn: "",
    linkedInUrl: "",
    cvFile: null,
    email: "",
    notes: "",
  });
  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(null);
  const [submitting, setSubmitting] = useState(false);
  const fieldRefs = {
    address: useRef(null),
    phone: useRef(null),
    nationality: useRef(null),
    preferredLanguage: useRef(null),
    hasLinkedIn: useRef(null),
    linkedInUrl: useRef(null),
    cvFile: useRef(null),
    email: useRef(null),
    notes: useRef(null),
  };

  const validate = {
    address: (v) => v.trim().length >= 8 ? "" : "Please enter a valid address.",
    phone: (v) => (/^\+?\d{10,15}$/).test(v) ? "" : "Enter a valid phone number.",
    nationality: (v) => v ? "" : "Select your nationality.",
    preferredLanguage: (v) => v ? "" : "Select a language.",
    hasLinkedIn: (v) => v ? "" : "Select yes or no.",
    linkedInUrl: (v) => {
      if (data.hasLinkedIn !== "yes") return "";
      return (/^https?:\/\/(www\.)?linkedin\.com\/(in|pub|company)\//i).test(v) ? "" : "Enter a valid LinkedIn URL.";
    },
    cvFile: (f) => f ? "" : "Upload your CV.",
    email: (v) => v && !/^\S+@\S+\.\S+$/.test(v) ? "Enter a valid email." : "",
  };

  const computeErrors = () => {
    const errs = {};
    for (let k in validate) {
      const msg = validate[k](data[k]);
      if (msg) errs[k] = msg;
    }
    return errs;
  };

  const onChange = (field, value) => {
    setData({ ...data, [field]: value });
    const msg = validate[field] ? validate[field](value) : "";
    setErrors({ ...errors, [field]: msg });
  };

  const onFile = (file) => {
    setData({ ...data, cvFile: file });
    setErrors({ ...errors, cvFile: validate.cvFile(file) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const errs = computeErrors();
    setErrors(errs);
    if (Object.keys(errs).length) {
      return;
    }
    setSubmitting(true);
    await new Promise(r => setTimeout(r, 1000));
    setSubmitted({ ...data, cvFileName: data.cvFile?.name });
    setSubmitting(false);
  };

  if (submitted) {
    return (
      <div className="page">
        <div className="card success">
          <h1>✅ Submission Received!</h1>
          <p>Thank you for submitting your details to HPAIR. We’ll be in touch soon.</p>
          <pre className="summary">{JSON.stringify(submitted, null, 2)}</pre>
          <div className="actions">
            <button className="secondary" onClick={() => setSubmitted(null)}>Edit</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="page">
      <header className="header">
        <img src="https://www.hpair.org/assets/images/logo.png" alt="HPAIR Logo" className="logo" />
        <h1>HPAIR Personal Information Form</h1>
        <p>Please complete all required fields carefully before submitting.</p>
      </header>

      <form className="card form" onSubmit={handleSubmit}>
        <label>Address*<textarea ref={fieldRefs.address} value={data.address} onChange={(e)=>onChange("address", e.target.value)} /></label>
        {errors.address && <p className="error">{errors.address}</p>}

        <div className="row two">
          <div>
            <label>Phone*<input ref={fieldRefs.phone} value={data.phone} onChange={(e)=>onChange("phone", e.target.value)} /></label>
            {errors.phone && <p className="error">{errors.phone}</p>}
          </div>
          <div>
            <label>Email<input ref={fieldRefs.email} value={data.email} onChange={(e)=>onChange("email", e.target.value)} /></label>
            {errors.email && <p className="error">{errors.email}</p>}
          </div>
        </div>

        <div className="row two">
          <div>
            <label>Nationality*<input ref={fieldRefs.nationality} value={data.nationality} onChange={(e)=>onChange("nationality", e.target.value)} /></label>
            {errors.nationality && <p className="error">{errors.nationality}</p>}
          </div>
          <div>
            <label>Preferred Language*<input ref={fieldRefs.preferredLanguage} value={data.preferredLanguage} onChange={(e)=>onChange("preferredLanguage", e.target.value)} /></label>
            {errors.preferredLanguage && <p className="error">{errors.preferredLanguage}</p>}
          </div>
        </div>

        <fieldset>
          <legend>Do you have LinkedIn?*</legend>
          <div className="radio-row">
            <label><input type="radio" checked={data.hasLinkedIn==="yes"} onChange={()=>onChange("hasLinkedIn","yes")} /> Yes</label>
            <label><input type="radio" checked={data.hasLinkedIn==="no"} onChange={()=>onChange("hasLinkedIn","no")} /> No</label>
          </div>
        </fieldset>
        {errors.hasLinkedIn && <p className="error">{errors.hasLinkedIn}</p>}

        {data.hasLinkedIn==="yes" && (
          <div>
            <label>LinkedIn URL<input ref={fieldRefs.linkedInUrl} value={data.linkedInUrl} onChange={(e)=>onChange("linkedInUrl", e.target.value)} /></label>
            {errors.linkedInUrl && <p className="error">{errors.linkedInUrl}</p>}
          </div>
        )}

        <label>CV Upload*<input type="file" ref={fieldRefs.cvFile} onChange={(e)=>onFile(e.target.files[0])} /></label>
        {errors.cvFile && <p className="error">{errors.cvFile}</p>}

        <label>Notes<textarea ref={fieldRefs.notes} value={data.notes} onChange={(e)=>onChange("notes", e.target.value)} /></label>

        <div className="actions">
          <button type="submit" disabled={submitting}>{submitting ? "Submitting..." : "Submit"}</button>
          <button type="reset" className="secondary" onClick={()=>setData({address:"",phone:"",nationality:"",preferredLanguage:"",hasLinkedIn:"",linkedInUrl:"",cvFile:null,email:"",notes:""})}>Reset</button>
        </div>
      </form>
    </div>
  );
}
