import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { FiUpload, FiX, FiFile } from 'react-icons/fi';

const FileUploadStep = ({ values, setFieldValue }) => {
  const [uploadProgress, setUploadProgress] = useState({});
  const [files, setFiles] = useState(values.files || []);

  const onDrop = (acceptedFiles) => {
    const newFiles = acceptedFiles.map(file => ({
      id: Date.now() + Math.random(),
      file,
      name: file.name,
      size: file.size,
      type: file.type,
      status: 'pending'
    }));

    const updatedFiles = [...files, ...newFiles];
    setFiles(updatedFiles);
    setFieldValue('files', updatedFiles);

    // Simulate file upload progress
    newFiles.forEach(fileObj => {
      simulateUpload(fileObj.id);
    });
  };

  const simulateUpload = (fileId) => {
    setUploadProgress(prev => ({ ...prev, [fileId]: 0 }));

    const interval = setInterval(() => {
      setUploadProgress(prev => {
        const current = prev[fileId] || 0;
        if (current >= 100) {
          clearInterval(interval);
          setFiles(prevFiles => 
            prevFiles.map(f => 
              f.id === fileId ? { ...f, status: 'completed' } : f
            )
          );
          return { ...prev, [fileId]: 100 };
        }
        return { ...prev, [fileId]: current + 10 };
      });
    }, 200);
  };

  const removeFile = (fileId) => {
    const updatedFiles = files.filter(f => f.id !== fileId);
    setFiles(updatedFiles);
    setFieldValue('files', updatedFiles);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif'],
      'application/pdf': ['.pdf'],
      'text/*': ['.txt', '.doc', '.docx']
    },
    maxSize: 5 * 1024 * 1024, // 5MB
    multiple: true
  });

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div>
      <h2>File Upload</h2>
      <p>Upload your documents and images. Maximum file size: 5MB per file.</p>

      <div
        {...getRootProps()}
        className={`file-upload-area ${isDragActive ? 'dragover' : ''}`}
      >
        <input {...getInputProps()} />
        <FiUpload size={48} color="#666" />
        <p>
          {isDragActive
            ? 'Drop the files here...'
            : 'Drag & drop files here, or click to select files'}
        </p>
        <p style={{ fontSize: '14px', color: '#666', marginTop: '8px' }}>
          Supported formats: Images (JPEG, PNG, GIF), PDF, Text files
        </p>
      </div>

      {files.length > 0 && (
        <div className="file-list">
          <h3>Uploaded Files ({files.length})</h3>
          {files.map((fileObj) => (
            <div key={fileObj.id} className="file-item">
              <div className="file-info">
                <FiFile size={20} color="#666" />
                <span className="file-name">{fileObj.name}</span>
                <span className="file-size">{formatFileSize(fileObj.size)}</span>
              </div>
              <div>
                {fileObj.status === 'pending' && (
                  <div className="progress-bar">
                    <div
                      className="progress-fill"
                      style={{ width: `${uploadProgress[fileObj.id] || 0}%` }}
                    />
                  </div>
                )}
                {fileObj.status === 'completed' && (
                  <span style={{ color: '#28a745', fontSize: '14px' }}>
                    ✓ Uploaded
                  </span>
                )}
                <button
                  type="button"
                  onClick={() => removeFile(fileObj.id)}
                  className="remove-file"
                >
                  <FiX />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FileUploadStep;
