import React, { useEffect, useMemo, useRef, useState } from "react";

// HPAIR Personal Information Form
// ------------------------------------------------------------
// Drop this file in your React app as src/App.jsx (or any route/component).
// Styling uses Tailwind classes (no imports needed in this canvas preview).
// Features implemented:
// - Real-time validation and inline errors
// - Prevent submit until all data is valid
// - Required fields (address, CV, phone, nationality, LinkedIn if user has one, preferred language)
// - Conditional question: LinkedIn URL shown only if user selects "Yes, I have LinkedIn"
// - Success and error submission states + loading spinner
// - Mobile-friendly + accessible labels/ARIA
// - Auto-save to localStorage (with debounce) and restore on load
// - Downloadable submission summary (JSON file)
// - Optional "Email my response" via mailto: (no backend)
// - Smooth keyboard navigation + focus first invalid field on submit
// - Extra niceties: file size/type validation, character counters, helper text
//
// If you want to actually email via a service, wire up EmailJS or a server endpoint
// where the submit() function is indicated.

const LANGS = [
  "English",
  "Mandarin Chinese",
  "Hindi",
  "Spanish",
  "French",
  "Arabic",
  "Bengali",
  "Portuguese",
  "Russian",
  "Japanese",
];

const NATIONALITIES = [
  "Afghan", "Albanian", "Algerian", "American", "Argentinian", "Australian", "Austrian",
  "Bangladeshi", "Belgian", "Brazilian", "British", "Canadian", "Chilean", "Chinese",
  "Colombian", "Cuban", "Czech", "Danish", "Dutch", "Egyptian", "Ethiopian", "Filipino",
  "Finnish", "French", "German", "Ghanaian", "Greek", "Guatemalan", "Hungarian",
  "Icelandic", "Indian", "Indonesian", "Iranian", "Iraqi", "Irish", "Israeli", "Italian",
  "Jamaican", "Japanese", "Jordanian", "Kenyan", "Korean", "Latvian", "Lebanese", "Lithuanian",
  "Malaysian", "Mexican", "Moroccan", "Nepalese", "New Zealander", "Nigerian", "Norwegian",
  "Pakistani", "Peruvian", "Polish", "Portuguese", "Romanian", "Russian", "Saudi", "Scottish",
  "Singaporean", "Slovak", "South African", "Spanish", "Sri Lankan", "Swedish", "Swiss",
  "Taiwanese", "Thai", "Turkish", "Ugandan", "Ukrainian", "Uruguayan", "Venezuelan", "Vietnamese",
];

const initialState = {
  address: "",
  phone: "",
  nationality: "",
  preferredLanguage: "",
  hasLinkedIn: "",
  linkedInUrl: "",
  cvFile: null, // File
  email: "", // for emailing the response to the applicant
  notes: "", // optional extra field to show creativity/usability
};

const STORAGE_KEY = "hpair_form_autosave_v1";

function useDebouncedCallback(cb, delay) {
  const timeoutRef = useRef(null);
  return (...args) => {
    if (timeoutRef.current) clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(() => cb(...args), delay);
  };
}

export default function App() {
  const [data, setData] = useState(() => {
    try {
      const raw = localStorage.getItem(STORAGE_KEY);
      if (!raw) return initialState;
      const parsed = JSON.parse(raw);
      return { ...initialState, ...parsed, cvFile: null }; // don't restore File objects
    } catch {
      return initialState;
    }
  });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(null); // stores a snapshot on success
  const [submitError, setSubmitError] = useState("");

  const fieldRefs = {
    address: useRef(null),
    phone: useRef(null),
    nationality: useRef(null),
    preferredLanguage: useRef(null),
    hasLinkedIn: useRef(null),
    linkedInUrl: useRef(null),
    cvFile: useRef(null),
    email: useRef(null),
    notes: useRef(null),
  };

  const validate = useMemo(() => ({
    address: (v) => v.trim().length >= 8 ? "" : "Please enter a full mailing address (min 8 characters).",
    phone: (v) => {
      const digits = v.replace(/\D/g, "");
      return digits.length >= 10 && digits.length <= 15 ? "" : "Enter a valid phone number (10–15 digits).";
    },
    nationality: (v) => v ? "" : "Select your nationality.",
    preferredLanguage: (v) => v ? "" : "Select a preferred language.",
    hasLinkedIn: (v) => v ? "" : "Please indicate if you have LinkedIn.",
    linkedInUrl: (v) => {
      if (data.hasLinkedIn !== "yes") return ""; // optional unless 'yes'
      const ok = /^https?:\/\/(www\.)?linkedin\.com\/(in|pub|company)\/[^\s]+$/i.test(v.trim());
      return ok ? "" : "Enter a valid LinkedIn profile URL (https://www.linkedin.com/in/...).";
    },
    cvFile: (file) => {
      if (!file) return "Please upload your CV (.pdf, .doc, .docx, max 5MB).";
      const allowed = ["application/pdf", "application/msword", "application/vnd.openxmlformats-officedocument.wordprocessingml.document"];
      if (!allowed.includes(file.type)) return "CV must be a PDF or Word document.";
      if (file.size > 5 * 1024 * 1024) return "File size exceeds 5MB.";
      return "";
    },
    email: (v) => {
      if (!v) return "Enter your email to receive a copy (optional but recommended).";
      const ok = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(v);
      return ok ? "" : "Enter a valid email address (name@example.com).";
    },
    notes: () => "",
  }), [data.hasLinkedIn]);

  const computeErrors = (form) => {
    const next = {};
    Object.entries(validate).forEach(([key, fn]) => {
      const msg = fn(form[key]);
      if (msg) next[key] = msg;
    });
    return next;
  };

  const isValid = useMemo(() => Object.keys(computeErrors(data)).length === 0, [data]);

  // Auto-save (debounced)
  const debouncedSave = useDebouncedCallback((payload) => {
    try {
      const { cvFile, ...rest } = payload; // exclude File object
      localStorage.setItem(STORAGE_KEY, JSON.stringify(rest));
    } catch {}
  }, 400);

  useEffect(() => {
    debouncedSave(data);
  }, [data]);

  const onChange = (field, value) => {
    setData((d) => ({ ...d, [field]: value }));
    setErrors((e) => {
      const copy = { ...e };
      const msg = validate[field] ? validate[field](value) : "";
      if (msg) copy[field] = msg; else delete copy[field];
      return copy;
    });
  };

  const onFile = (file) => {
    const msg = validate.cvFile(file);
    setData((d) => ({ ...d, cvFile: msg ? null : file }));
    setErrors((e) => ({ ...e, cvFile: msg }));
  };

  const focusFirstInvalid = (errs) => {
    const order = [
      "address","phone","nationality","preferredLanguage","hasLinkedIn","linkedInUrl","cvFile","email","notes"
    ];
    for (const k of order) {
      if (errs[k]) {
        fieldRefs[k]?.current?.focus?.();
        break;
      }
    }
  };

  const submit = async (evt) => {
    evt.preventDefault();
    const errs = computeErrors(data);
    setErrors(errs);
    if (Object.keys(errs).length) {
      focusFirstInvalid(errs);
      return;
    }
    setSubmitting(true);
    setSubmitError("");

    try {
      // Simulate async submission (replace with real API call)
      await new Promise((res) => setTimeout(res, 1200));

      const snapshot = {
        ...data,
        cvFileName: data.cvFile?.name || "",
        cvFileSize: data.cvFile?.size || 0,
        submittedAt: new Date().toISOString(),
      };
      setSubmitted(snapshot);

      // Optional: open default mail client with a prefilled email to the applicant
      if (data.email) {
        const subject = encodeURIComponent("HPAIR Form Submission Confirmation");
        const body = encodeURIComponent(
          `Thank you for your submission!\n\nSummary:\n${JSON.stringify(snapshot, null, 2)}`
        );
        // comment the next line out if not desired
        // window.open(`mailto:${data.email}?subject=${subject}&body=${body}`);
      }

      // Clear autosave but keep success state visible
      localStorage.removeItem(STORAGE_KEY);
    } catch (e) {
      setSubmitError("Something went wrong while submitting. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  const downloadSummary = () => {
    const snapshot = {
      ...data,
      cvFileName: data.cvFile?.name || "",
      cvFileSize: data.cvFile?.size || 0,
      generatedAt: new Date().toISOString(),
    };
    const blob = new Blob([JSON.stringify(snapshot, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "hpair_submission_summary.json";
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  };

  if (submitted) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="max-w-2xl w-full bg-white shadow-xl rounded-2xl p-6">
          <div className="flex items-center gap-3 mb-4">
            <span className="i success inline-flex h-10 w-10 items-center justify-center rounded-full bg-green-100">✅</span>
            <h1 className="text-2xl font-semibold">Submission received!</h1>
          </div>
          <p className="text-gray-600 mb-6">Thank you. We saved your information. You can download a summary below or go back to edit.</p>

          <div className="bg-gray-50 rounded-xl p-4 max-h-72 overflow-auto text-sm font-mono">
            <pre>{JSON.stringify(submitted, null, 2)}</pre>
          </div>

          <div className="mt-6 flex flex-col sm:flex-row gap-3">
            <button onClick={downloadSummary} className="px-4 py-2 rounded-xl shadow hover:shadow-md active:scale-95 transition w-full sm:w-auto bg-gray-900 text-white">Download summary</button>
            <button onClick={() => setSubmitted(null)} className="px-4 py-2 rounded-xl shadow hover:shadow-md active:scale-95 transition w-full sm:w-auto bg-white border">Edit information</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <main className="min-h-screen bg-gradient-to-b from-white to-gray-50 p-4">
      <div className="max-w-3xl mx-auto">
        <header className="mb-6">
          <h1 className="text-3xl font-bold tracking-tight">HPAIR — Personal Information</h1>
          <p className="text-gray-600 mt-1">Please complete all required fields. Your progress auto-saves.</p>
        </header>

        {submitError && (
          <div className="mb-4 rounded-xl border border-red-200 bg-red-50 p-3 text-red-700">{submitError}</div>
        )}

        <form onSubmit={submit} className="bg-white shadow-xl rounded-2xl p-6 grid gap-5">
          {/* Address */}
          <div>
            <label htmlFor="address" className="block font-medium">Address <span className="text-red-500">*</span></label>
            <textarea
              id="address"
              ref={fieldRefs.address}
              required
              value={data.address}
              onChange={(e) => onChange("address", e.target.value)}
              className={`mt-1 w-full rounded-xl border p-3 focus:outline-none focus:ring-2 ${errors.address ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
              rows={3}
              aria-invalid={!!errors.address}
              aria-describedby={errors.address ? "address-error" : undefined}
              placeholder="Street, City, State/Province, Postal code, Country"
            />
            {errors.address && <p id="address-error" className="mt-1 text-sm text-red-600">{errors.address}</p>}
          </div>

          {/* Phone & Email */}
          <div className="grid sm:grid-cols-2 gap-4">
            <div>
              <label htmlFor="phone" className="block font-medium">Phone number <span className="text-red-500">*</span></label>
              <input
                id="phone"
                ref={fieldRefs.phone}
                type="tel"
                inputMode="tel"
                value={data.phone}
                onChange={(e) => onChange("phone", e.target.value)}
                className={`mt-1 w-full rounded-xl border p-3 focus:outline-none focus:ring-2 ${errors.phone ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
                placeholder="e.g., +1 617 555 1234"
                aria-invalid={!!errors.phone}
                aria-describedby={errors.phone ? "phone-error" : undefined}
              />
              {errors.phone && <p id="phone-error" className="mt-1 text-sm text-red-600">{errors.phone}</p>}
            </div>

            <div>
              <label htmlFor="email" className="block font-medium">Email (for confirmation)</label>
              <input
                id="email"
                ref={fieldRefs.email}
                type="email"
                value={data.email}
                onChange={(e) => onChange("email", e.target.value)}
                className={`mt-1 w-full rounded-xl border p-3 focus:outline-none focus:ring-2 ${errors.email ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
                placeholder="name@example.com"
                aria-invalid={!!errors.email}
                aria-describedby={errors.email ? "email-error" : undefined}
              />
              {errors.email && <p id="email-error" className="mt-1 text-sm text-red-600">{errors.email}</p>}
            </div>
          </div>

          {/* Nationality & Preferred Language */}
          <div className="grid sm:grid-cols-2 gap-4">
            <div>
              <label htmlFor="nationality" className="block font-medium">Nationality <span className="text-red-500">*</span></label>
              <select
                id="nationality"
                ref={fieldRefs.nationality}
                value={data.nationality}
                onChange={(e) => onChange("nationality", e.target.value)}
                className={`mt-1 w-full rounded-xl border p-3 bg-white focus:outline-none focus:ring-2 ${errors.nationality ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
                aria-invalid={!!errors.nationality}
                aria-describedby={errors.nationality ? "nationality-error" : undefined}
              >
                <option value="">Select...</option>
                {NATIONALITIES.map((n) => <option key={n} value={n}>{n}</option>)}
              </select>
              {errors.nationality && <p id="nationality-error" className="mt-1 text-sm text-red-600">{errors.nationality}</p>}
            </div>

            <div>
              <label htmlFor="preferredLanguage" className="block font-medium">Preferred language <span className="text-red-500">*</span></label>
              <select
                id="preferredLanguage"
                ref={fieldRefs.preferredLanguage}
                value={data.preferredLanguage}
                onChange={(e) => onChange("preferredLanguage", e.target.value)}
                className={`mt-1 w-full rounded-xl border p-3 bg-white focus:outline-none focus:ring-2 ${errors.preferredLanguage ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
                aria-invalid={!!errors.preferredLanguage}
                aria-describedby={errors.preferredLanguage ? "preferredLanguage-error" : undefined}
              >
                <option value="">Select...</option>
                {LANGS.map((l) => <option key={l} value={l}>{l}</option>)}
              </select>
              {errors.preferredLanguage && <p id="preferredLanguage-error" className="mt-1 text-sm text-red-600">{errors.preferredLanguage}</p>}
            </div>
          </div>

          {/* LinkedIn Conditional */}
          <div>
            <fieldset className="">
              <legend className="block font-medium">Do you have a LinkedIn profile? <span className="text-red-500">*</span></legend>
              <div className="mt-2 flex gap-6">
                <label className="inline-flex items-center gap-2">
                  <input
                    type="radio"
                    name="hasLinkedIn"
                    ref={fieldRefs.hasLinkedIn}
                    checked={data.hasLinkedIn === "yes"}
                    onChange={() => onChange("hasLinkedIn", "yes")}
                  />
                  <span>Yes</span>
                </label>
                <label className="inline-flex items-center gap-2">
                  <input
                    type="radio"
                    name="hasLinkedIn"
                    checked={data.hasLinkedIn === "no"}
                    onChange={() => onChange("hasLinkedIn", "no")}
                  />
                  <span>No</span>
                </label>
              </div>
            </fieldset>
            {errors.hasLinkedIn && <p className="mt-1 text-sm text-red-600">{errors.hasLinkedIn}</p>}
          </div>

          {data.hasLinkedIn === "yes" && (
            <div>
              <label htmlFor="linkedInUrl" className="block font-medium">LinkedIn URL</label>
              <input
                id="linkedInUrl"
                ref={fieldRefs.linkedInUrl}
                type="url"
                value={data.linkedInUrl}
                onChange={(e) => onChange("linkedInUrl", e.target.value)}
                className={`mt-1 w-full rounded-xl border p-3 focus:outline-none focus:ring-2 ${errors.linkedInUrl ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
                placeholder="https://www.linkedin.com/in/your-handle"
                aria-invalid={!!errors.linkedInUrl}
                aria-describedby={errors.linkedInUrl ? "linkedin-error" : undefined}
              />
              {errors.linkedInUrl && <p id="linkedin-error" className="mt-1 text-sm text-red-600">{errors.linkedInUrl}</p>}
            </div>
          )}

          {/* CV Upload */}
          <div>
            <label htmlFor="cvFile" className="block font-medium">CV (PDF/Word, max 5MB) <span className="text-red-500">*</span></label>
            <input
              id="cvFile"
              ref={fieldRefs.cvFile}
              type="file"
              accept=".pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
              onChange={(e) => onFile(e.target.files?.[0] || null)}
              className={`mt-1 w-full rounded-xl border p-3 bg-white focus:outline-none focus:ring-2 ${errors.cvFile ? "border-red-400 focus:ring-red-300" : "border-gray-200 focus:ring-gray-300"}`}
              aria-invalid={!!errors.cvFile}
              aria-describedby={errors.cvFile ? "cv-error" : undefined}
            />
            <div className="text-sm text-gray-600 mt-1">{data.cvFile ? `${data.cvFile.name} — ${(data.cvFile.size/1024/1024).toFixed(2)}MB` : "No file selected."}</div>
            {errors.cvFile && <p id="cv-error" className="mt-1 text-sm text-red-600">{errors.cvFile}</p>}
          </div>

          {/* Notes (optional) */}
          <div>
            <label htmlFor="notes" className="block font-medium">Notes (optional)</label>
            <textarea
              id="notes"
              ref={fieldRefs.notes}
              value={data.notes}
              onChange={(e) => onChange("notes", e.target.value.slice(0, 500))}
              className="mt-1 w-full rounded-xl border border-gray-200 p-3 focus:outline-none focus:ring-2 focus:ring-gray-300"
              rows={4}
              placeholder="Anything else we should know? (max 500 characters)"
            />
            <div className="text-xs text-gray-500 mt-1">{data.notes.length}/500</div>
          </div>

          {/* Actions */}
          <div className="flex flex-col sm:flex-row gap-3 justify-between">
            <div className="flex gap-3">
              <button
                type="submit"
                disabled={!isValid || submitting}
                className={`px-4 py-2 rounded-xl shadow hover:shadow-md active:scale-95 transition ${(!isValid || submitting) ? "bg-gray-300 text-gray-600 cursor-not-allowed" : "bg-gray-900 text-white"}`}
              >
                {submitting ? "Submitting…" : "Submit"}
              </button>
              <button
                type="button"
                onClick={downloadSummary}
                className="px-4 py-2 rounded-xl border bg-white hover:shadow"
              >
                Download summary
              </button>
            </div>

            <button
              type="button"
              onClick={() => { localStorage.removeItem(STORAGE_KEY); setData(initialState); setErrors({}); }}
              className="px-4 py-2 rounded-xl border bg-white hover:shadow"
            >
              Reset form
            </button>
          </div>

          {/* Live Validity Footer */}
          <div className="text-sm text-gray-600 flex items-center gap-2">
            <span className={`inline-block h-2.5 w-2.5 rounded-full ${isValid ? "bg-green-500" : "bg-red-500"}`}></span>
            {isValid ? "All required fields look good." : "Please fix the highlighted fields before submitting."}
          </div>
        </form>

        {/* Tiny footer */}
        <p className="text-xs text-gray-500 mt-6">Tip: Use Tab/Shift+Tab to navigate quickly. Your progress is saved locally and cleared on successful submission.</p>
      </div>

      {/* Submitting overlay */}
      {submitting && (
        <div className="fixed inset-0 bg-black/20 backdrop-blur-sm flex items-center justify-center">
          <div className="bg-white rounded-2xl shadow-xl px-6 py-5 text-center">
            <div className="animate-spin h-8 w-8 border-4 border-gray-200 border-t-gray-900 rounded-full mx-auto mb-3"></div>
            <p className="font-medium">Submitting your information…</p>
          </div>
        </div>
      )}
    </main>
  );
}
