import React, { useState } from 'react';
import PersonalInfoStep from './steps/PersonalInfoStep';
import ContactInfoStep from './steps/ContactInfoStep';
import FileUploadStep from './steps/FileUploadStep';
import PreferencesStep from './steps/PreferencesStep';
import ReviewStep from './steps/ReviewStep';
import StepIndicator from './StepIndicator';

const MultiStepForm = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({});

  const steps = [
    { id: 1, title: 'Personal Info', component: PersonalInfoStep },
    { id: 2, title: 'Contact Info', component: ContactInfoStep },
    { id: 3, title: 'File Upload', component: FileUploadStep },
    { id: 4, title: 'Preferences', component: PreferencesStep },
    { id: 5, title: 'Review', component: ReviewStep },
  ];

  // TODO: Implement form validation using Formik and Yup
  // TODO: Implement step navigation logic
  // TODO: Implement form data persistence across steps
  // TODO: Implement form submission

  const handleNext = () => {
    // TODO: Add validation before moving to next step
    if (currentStep < steps.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = () => {
    // TODO: Implement form submission
    console.log('Form submitted:', formData);
    alert('Form submitted successfully! Check the console for data.');
  };

  const CurrentStepComponent = steps[currentStep - 1].component;

  return (
    <div className="container">
      <div className="form-container">
        <StepIndicator currentStep={currentStep} steps={steps} />

        <form>
          <CurrentStepComponent />

          <div className="form-actions">
            {currentStep > 1 && (
              <button
                type="button"
                onClick={handlePrevious}
                className="btn btn-secondary"
              >
                Previous
              </button>
            )}

            <button
              type="button"
              onClick={currentStep === steps.length ? handleSubmit : handleNext}
              className="btn btn-primary"
            >
              {currentStep === steps.length ? 'Submit' : 'Next'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default MultiStepForm;
